  return (
    <div>
      <div>
        {isCreated ?
          <CreateForm
            state={state}
            setState={setState}
            setIsSubmitted={setIsSubmitted}
          />
          :
          <Home
            state={state}
            setState={setState}
            setIsSubmitted={setIsSubmitted}
          />}
      </div>

      <div>
        {isSubmitted ?
          <MyForm state={state} />
          :
          <CreateForm
          state={state}
          setState={setState}
          setIsSubmitted={setIsSubmitted}
        />}
