import React, {useState, useEffect} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import { Button, Form, Col, Row, ListGroup, Modal, FormGroup } from 'react-bootstrap';
import './Home.css'
import { CustomModal } from "../components/modal";


export default function Home({ state, setState, setIsCreated }) {
    const [selectedCourse, setSelectedCourse] = useState(null);
    const [courseList, setCourseList] = useState([{
        courseId: "ENSF607",
        courseName: "Software Design",
        courseDescription: "A course to learn about software design.",
        hours: "6",
        courseCredit: "3",
        reference: "www.google.ca"

    }]);

    // useEffect(() => {
    //     fetch('path/to/list/api')
    //         .then((res) => res.json())
    //         .then((res) => setCourseList(res))
    // },[])

    const handleCourseClick = (e) => {
        console.log(e)
        setSelectedCourse(e)
    }

    const handleSave = () => {
        setCourseList(courseList.map((element) => (element.courseId === selectedCourse.courseId)
            ? selectedCourse : element));
        setSelectedCourse(null);
    }

    const handleDelete = () => {
        // delete in backend
        setCourseList(courseList.filter((element) => element.courseId !== selectedCourse.courseId))
        setSelectedCourse(null);
    }

    const handleAddCourse = () => {
        setCourseList([...courseList, selectedCourse])
    }


    return (
        <div>
            <ListGroup>
                {courseList.map((course) => (
                    <ListGroup.Item
                        key={course.courseId}
                        onClick={() => handleCourseClick(course)}
                    >
                        {course.courseName}
                </ListGroup.Item>))}
            </ListGroup>
            <CustomModal
                selectedCourse={selectedCourse}
                setSelectedCourse={setSelectedCourse}
                courseList={courseList}
                setCourseList = {setCourseList}
            />
            <h1 className='title'>Hello on the main page</h1>
            <div className='search'>
                <Form.Group as={Row} controlId="formHorizontalReference">
                    <Col sm={10} className='horizontalInput'>
                        <Form.Control type="text" placeholder="Search for course by Id"


                        />
                    </Col>
                </Form.Group>
                <Button

                type="button"
                variant="primary"
                onClick={(e) => {



                }}
            > Search</Button>
            </div>

            <div className='createButton'>


             <Button

                type="button"
                variant="primary"
                onClick={(e) => {
                    e.preventDefault();
                    setIsCreated(true);
                }}
                > 
                Create Course Outline
                </Button>
            </div>    
        </div>
    )   
}
