import React, {useState, useEffect} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import { Button, Form, Col, Row, ListGroup, Modal, FormGroup } from 'react-bootstrap';
import './Home.css'


export default function Home({ state, setState, setIsCreated }) {
    const [selectedCourse, setSelectedCourse] = useState(null);
    const [courseList, setCourseList] = useState([{
        courseId: "ENSF607",
        courseName: "Software Design",
        courseDescription: "A course to learn about software design.",
        hours: "6",
        courseCredit: "3",
        reference: "www.google.ca"

    }]);

    // useEffect(() => {
    //     fetch('path/to/list/api')
    //         .then((res) => res.json())
    //         .then((res) => setCourseList(res))
    // },[])

    const handleCourseClick = (e) => {
        console.log(e)
        setSelectedCourse(e)
    }

    const handleSave = () => {
        setCourseList(courseList.map((element) => (element.courseId === selectedCourse.courseId)
            ? selectedCourse : element));
        setSelectedCourse(null);
    }


    return (
        <div>
            <ListGroup>
                {courseList.map((course) => (
                    <ListGroup.Item
                        key={course.courseId}
                        onClick={() => handleCourseClick(course)}
                    >
                        {course.courseName}
                </ListGroup.Item>))}
            </ListGroup>
            <Modal
                show={selectedCourse !== null}
                onHide={() => setSelectedCourse(null)}
                backdrop="static"
                // keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Modal title</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <FormGroup onSubmit={(e) => {
                            e.preventDefault();
                            console.log(e)
                        }}>
                        {selectedCourse && Object.entries(selectedCourse).map(([key, value]) => (
                            <>
                                <Form.Label>{key}</Form.Label>
                            <Form.Control
                                key={`modal-${key}`}
                                placeholder={value}
                                    title={key}
                                    disabled={key === 'courseId'}
                                onChange={(e) => {
                                    setSelectedCourse({...selectedCourse, [key]:e.target.value})
                                }}
                                />
                                </>

                        ))}
                    </FormGroup>
                    </Form>


                    I will not close if you click outside me. Don't even try to press
                    escape key.
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleSave}>
                        Save
                    </Button>
                    <Button variant="primary">Delete</Button>
                </Modal.Footer>
            </Modal>
            <h1 className='title'>Hello on the main page</h1>
            <div className='search'>
                <Form.Group as={Row} controlId="formHorizontalReference">
                    <Col sm={10} className='horizontalInput'>
                        <Form.Control type="text" placeholder="Search for course by Id"


                        />
                    </Col>
                </Form.Group>
                <Button

                type="button"
                variant="primary"
                onClick={(e) => {



                }}
            > Search</Button>
            </div>

            <div className='createButton'>


             <Button

                type="button"
                variant="primary"
                onClick={(e) => {
                    e.preventDefault();
                    setIsCreated(true);
                }}
                > 
                Create Course Outline
                </Button>
            </div>    
        </div>
    )   
}
