import React, {useState, useEffect} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import { Button, Form, Col, Row} from 'react-bootstrap';

export default function Home({ state, setState, setIsCreated}) {
    return (
        <div>
            <h1>Hello on the main page</h1>
            <div>
                <Form.Group as={Row} controlId="formHorizontalReference">
                    <Form.Label column sm={2}>
                    Academic Reference:
                    </Form.Label>
                    <Col sm={10} className='horizontalInput'>
                        <Form.Control type="text" placeholder="Search for course by Id"


                        />
                    </Col>
                </Form.Group>
                <Button

                type="button"
                variant="primary"
                onClick={(e) => {
                    e.preventDefault();
                    setIsCreated(true);


                }}
            > Search</Button>
            </div>


             <Button

                type="button"
                variant="primary"
                onClick={(e) => {
                    e.preventDefault();
                    setIsCreated(true);


                }}
            > Create Course Outline</Button>
        </div>
    )   
}
